package cn.com.evo.cms.constant;/** * 支付方式 枚举 */public enum PayTypeEnum {    WeiXin(1), ALi(2), BOSS(3), SP(4) , Union(5);    private Integer type;    private PayTypeEnum(Integer type) {        this.type = type;    }    public Integer getType() {        return type;    }    public void setType(Integer type) {        this.type = type;    }    public static PayTypeEnum getByType(Integer type) {        for (PayTypeEnum payTypeEnum : values()) {            if (payTypeEnum.getType() == type) {                return payTypeEnum;            }        }        return null;    }}