package cn.com.evo.cms.constant;/** * 省网code 枚举 */public enum ProvinceCodeEnum {    /**     * 新疆广电省网     */    XinJiang("10001", 1, "xjnt"),    /**     * 四川移动     */    SiChuanYiDong("10002", 2, "scyd"),    /**     * 龙江网络     */    Longjiang("10003", 3, "lj"),    /**     * 未来媒体     */    Evo("10004", 4, "evo"),    /**     * 华数     */    Wasu("10005", 5, "wasu"),    /**     * 济南有线     */    JinanYouxian("10006", 6, "jnyx"),    /**     * 机房102服务器     */    Local102("10007", 7, "local102"),    /**     * 广西广电     */    GuangXi("10008", 8, "guangxi"),    /**     * 宁夏     */    NingXia("10009", 8, "ningxia"),    /**     * 重庆有线     */    ChongQing("10010", 9, "chongqing"),    /**     * 青海     */    QingHai("10011", 10, "qingHai"),    /**     * 深圳天威     */    ShenZhen("10012", 11, "shenzhen"),    /**     * 新疆tvos     */    XJTvos("10013", 12, "xjTvos"),    /**     * 错误     */    ERROR("", -1, "error");    private String code;    private Integer value;    private String name;    ProvinceCodeEnum(String code, Integer value, String name) {        this.code = code;        this.value = value;        this.name = name;    }    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getValue() {        return value;    }    public void setValue(Integer value) {        this.value = value;    }    /**     * 根据value返回枚举类型,主要在switch中使用     */    public static ProvinceCodeEnum getByName(String name) {        for (ProvinceCodeEnum provinceCodeEnum : values()) {            if (provinceCodeEnum.getName().equals(name)) {                return provinceCodeEnum;            }        }        return ERROR;    }}