package cn.com.evo.cms.domain.entity.activity;import com.frameworks.core.entity.BaseEntity;import lombok.Data;import lombok.EqualsAndHashCode;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.NamedQuery;import javax.persistence.Table;/** * @Description: * @author: lu.xin * @create: 2019-04-23 9:51 AM **/@Data@EqualsAndHashCode(callSuper = false)@Entity@Table(name = "activity_draw_query")@NamedQuery(name = "DrawQuery.findAll", query = "SELECT a FROM DrawQuery a")public class DrawQuery extends BaseEntity {    private static final long serialVersionUID = 1L;    @Column(columnDefinition = "varchar(32) COMMENT '抽奖人id'")    private String userId;    @Column(columnDefinition = "varchar(32) COMMENT '智能卡号'")    private String cardNo;    @Column(columnDefinition = "varchar(32) COMMENT '抽奖id'")    private String drawId;    @Column(columnDefinition = "varchar(32) COMMENT '中奖商品id'")    private String drawChildId;    @Column(columnDefinition = "int(2) COMMENT '是否中奖；0-否；1-是'")    private Integer isOwn;    @Column(columnDefinition = "int(2) COMMENT '是否发货；0-否；1-是'")    private Integer isSend;    @Column(columnDefinition = "varchar(11) COMMENT '收货人手机号'")    private String phone;    @Column(columnDefinition = "varchar(254) COMMENT '收货地址'")    private String address;    @Column(columnDefinition = "varchar(254) COMMENT '物流单号'")    private String orderNo;    @Column(columnDefinition = "varchar(32) COMMENT '应用id'")    private String appId;}