package cn.com.evo.cms.domain.entity.pay;import com.frameworks.core.entity.BaseEntity;import lombok.Data;import lombok.EqualsAndHashCode;import javax.persistence.*;/** * @Description: 限时免费 * @author: lu.xin * @create: 2019-01-16 8:28 PM **/@Data@EqualsAndHashCode(callSuper = false)@Entity@Table(name = "pay_limit_free")@NamedQuery(name = "LimitFree.findAll", query = "SELECT c FROM LimitFree c")public class LimitFree extends BaseEntity {    /**     * 业务id     */    @Column(columnDefinition = "varchar(32) comment '业务id；表示：应用id，栏目id，内容id'")    private String bizId;    /**     * 限免开始时间     */    @Column(columnDefinition = "varchar(32) comment '限免开始时间'")    private String beginFreeTime;    /**     * 限免结束时间     */    @Column(columnDefinition = "varchar(32) comment '限免结束时间'")    private String endFreeTime;    /**     * 限免类型 限免类型：1-应用限免；2-栏目限免；3-内容限免     */    @Column(columnDefinition = "int(2) comment '限免类型：1-应用限免；2-栏目限免；3-内容限免'")    private Integer type;    /**     * 内容类型     * <p>     * movie("电影", 1),     * episode("剧集", 2),     * live("直播", 3),     * active("活动", 4),     * book("图书", 5);     */    @Column(columnDefinition = "int(2) comment '内容类型：'")    private Integer contentType;    /**     * 应用id，当限免类型是 内容限免时，需要指定应用id。     */    @Column(columnDefinition = "varchar(32) comment '应用id：'")    private String appId;    @Transient    private String columnId;    @Transient    private String contentId;}