package cn.com.evo.cms.domain.enums;/** * @Description: 支付-周期枚举类 * @author: luxin **/public enum UnitEnum {    /**     * 错误     */    error("错误", -1),    /**     * 分钟     */    min("分钟", 0),    /**     * 小时     */    hour("小时", 1),    /**     * 天     */    day("天", 2),    /**     * 月     */    month("月", 3),    /**     * 年     */    year("年", 4),    /**     * 次     */    count("次", 5),    /**     * 本     */    book("本", 6);    String name;    Integer value;    UnitEnum(String name, int value) {        this.name = name;        this.value = value;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getValue() {        return value;    }    public void setValue(Integer value) {        this.value = value;    }    public static UnitEnum getByValue(Integer value) {        if (value.equals(min.value)) {            return min;        } else if (value.equals(hour.value)) {            return hour;        } else if (value.equals(day.value)) {            return day;        } else if (value.equals(month.value)) {            return month;        } else if (value.equals(year.value)) {            return year;        } else if (value.equals(count.value)) {            return count;        } else if (value.equals(book.value)) {            return book;        } else {            return error;        }    }}