package cn.com.evo.cms.domain.vo.cms;import com.frameworks.core.web.constants.WebConsts;import com.frameworks.core.web.search.SearchFilter;import com.frameworks.utils.ServletUtils;import lombok.Data;import lombok.EqualsAndHashCode;import javax.servlet.http.HttpServletRequest;import java.util.Collection;import java.util.Map;/** * @Description: 内容检索vo * @author: lu.xin * @create: 2019-01-16 5:45 PM **/@Data@EqualsAndHashCode(callSuper = false)public class ContentSearchVo {    private String aId;    private Integer type;    private String contentName;    private Integer contentType;    private Integer enable;    public ContentSearchVo(HttpServletRequest request) {        Collection<SearchFilter> searchFilters = getSearchFilter(request);        for (SearchFilter searchFilter : searchFilters) {            String fieldName = searchFilter.getFieldName();            String value = searchFilter.getValue().toString();            if ("aId".equals(fieldName)) {                this.aId = value;            }            if ("type".equals(fieldName)) {                this.type = Integer.valueOf(value);            }            if ("contentName".equals(fieldName)) {                this.contentName = value;            }            if ("contentType".equals(fieldName)) {                this.contentType = Integer.valueOf(value);            }            if ("publish".equals(fieldName)) {                this.enable = Integer.valueOf(value);            }        }    }    /**     * 请求转换查询条件     *     * @param request     * @return     */    public static Collection<SearchFilter> getSearchFilter(HttpServletRequest request) {        Map<String, Object> searchParams = ServletUtils.getParametersStartingWith(request, WebConsts.SEARCH_PREFIX);        Map<String, SearchFilter> filters = SearchFilter.parse(searchParams);        return filters.values();    }}