package cn.com.evo.cms.service.cms;import cn.com.evo.cms.domain.entity.cms.Video;import org.springframework.web.multipart.MultipartFile;/** * @Description: 省网注入服务接口 * @author: lu.xin * @create: 2019-03-11 11:10 AM **/public interface SpService {    /**     * 拉取资源你数据     */    void pullResource();    /**     * 电影内容注入统一入口     *     * @param contentId 内容id     * @param action    动作     */    void synMovie(String contentId, String action);    /**     * 批量电影内容注入统一入口     *     * @param contentIds 内容ids     * @param action     动作     */    void synMovies(String[] contentIds, String action);    /**     * 剧集内容注入统一入口     *     * @param contentId 内容id     * @param action    动作     */    void synEpisode(String contentId, String action);    /**     * 批量剧集内容注入统一入口     *     * @param contentIds 内容ids     * @param action     动作     */    void synEpisodes(String[] contentIds, String action);    /**     * 剧集内容注入统一入口     *     * @param contentId 内容id(子集id)     * @param action    动作     */    void synEpisodeChild(String contentId, String action);    /**     * 批量剧集内容注入统一入口     *     * @param contentIds 内容ids(子集id)     * @param action     动作s     */    void synEpisodeChilds(String[] contentIds, String action);    /**     * 语音搜索数据同步     *     * @param catalogueRelationId     */    void voiceSyn(String catalogueRelationId);    /**     * 获取播放地址     *     * @param catalogueRelationId 栏目内容关系id     * @param episodeNumber       子集集数     * @param accesstoken         用户登录标识（茁壮homed平台）     * @param appId               应用id     * @param platform            平台来源     * @return     */    Video getPlayUrl(String catalogueRelationId, Integer episodeNumber, String accesstoken, String appId, String platform);    /**     * 获取播放地址     *     * @param contentId   内容id     * @param accesstoken 用户登录标识（茁壮homed平台）     * @param appId               应用id     * @param platform            平台来源     * @return     */    Video getPlayUrl(String contentId, String accesstoken, String appId, String platform);    /**     * 新疆电信特殊逻辑。片单导入并直接注入cp     * @param files     */    void registMovieForXjdx(MultipartFile[] files);    /**     * 统一发布接口     * @param contentId     */    void publish(String contentId);    /**     * 统一取消发布接口     * @param contentId     */    void unPublish(String contentId);    /**     * 统一绑定产品包接口(局方)     * @param contentId     */    void bindProduct(String contentId, Integer type);    /**     * 统一删除媒资(当不允许使用注入标签删除时)     * @param contentId     */    void delete(String contentId);    /**     * 批量发布     * @param contentIds     */    void publishs(String[] contentIds);    /**     * 批量取消发布     * @param contentIds     */    void unPublishs(String[] contentIds);    /**     * 批量绑定产品包     * @param contentIds     */    void bindProducts(String[] contentIds, Integer type);    /**     * 批量删除媒资     */    void deletes(String[] contentIds);}