package cn.com.evo.cms.web.api;import cn.com.evo.integration.wasu.sdk.PaySDK;import com.frameworks.core.web.controller.BaseController;import org.springframework.web.bind.annotation.*;/** * @Description: 测试接口。用于接口测试临时使用 * @author: lu.xin * @create: 2019-05-28 2:30 PM **/@RestController@RequestMapping("/api/test")@CrossOrigin(origins = "*", maxAge = 3600)public class TestApi extends BaseController {    /**     * @return     */    @RequestMapping(value = "/wasu/pay", method = {RequestMethod.GET, RequestMethod.POST})    public Object test(@RequestParam("ppvId") String ppvId,                       @RequestParam("payMoney") double payMoney,                       @RequestParam("productName") String productName,                       @RequestParam("custName")String custName,                       @RequestParam("custId")String custId,                       @RequestParam("cardNo")String cardNo,                       @RequestParam("area")String area,                       @RequestParam("city")String city) {        PaySDK.pay(ppvId, payMoney, productName, custName, custId, cardNo, area, city);        return "hello";    }}