package cn.com.evo.cms.web.api.vo;import cn.com.evo.cms.domain.entity.activity.DrawChild;import cn.com.evo.cms.domain.entity.activity.DrawQuery;import java.util.Date;/** * @Description: * @author: lu.xin * @create: 2019-04-23 5:51 PM **/public class DrawQueryApiVo {    private String id;    /**     * 智能卡号     */    private String cardNo;    /**     * 奖品名称     */    private String name;    /**     * 是否中奖；0-否；1-是     */    private Integer isOwn;    /**     * 是否发货；0-否；1-是     */    private Integer isSend;    /**     * 排序     */    private Integer sort;    /**     * 时间     */    private Date createDate;    public DrawQueryApiVo(DrawQuery drawQuery, DrawChild drawChild) {        this.id = drawQuery.getId();        this.name = drawChild.getName();        this.cardNo = drawQuery.getCardNo();        this.isOwn = drawQuery.getIsOwn();        this.isSend = drawQuery.getIsSend();        this.sort = drawChild.getSort();        this.createDate = drawQuery.getCreateDate();    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getIsOwn() {        return isOwn;    }    public void setIsOwn(Integer isOwn) {        this.isOwn = isOwn;    }    public Integer getIsSend() {        return isSend;    }    public void setIsSend(Integer isSend) {        this.isSend = isSend;    }    public String getCardNo() {        return cardNo;    }    public void setCardNo(String cardNo) {        this.cardNo = cardNo;    }    public Integer getSort() {        return sort;    }    public void setSort(Integer sort) {        this.sort = sort;    }    public Date getCreateDate() {        return createDate;    }    public void setCreateDate(Date createDate) {        this.createDate = createDate;    }}