package cn.com.evo.integration.common;import cn.com.evo.admin.manage.domain.entity.ProvinceConstant;import cn.com.evo.admin.manage.service.ProvinceConstantService;import com.google.common.collect.Maps;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import javax.annotation.PostConstruct;import java.util.List;import java.util.Map;/** * @Description: 省网常量类 * @author: lu.xin * @create: 2019-03-28 2:11 PM **/@Componentpublic class ConstantFactory {    protected Logger logger = LogManager.getLogger(this.getClass());    /**     * 是否已启动     */    private static boolean isRun = false;    public static Map<String, String> map = Maps.newHashMap();    @Autowired    private ProvinceConstantService provinceConstantService;    @PostConstruct    public void run() {        if (!isRun) {            logger.error("省网常量类初始化...");            isRun = true;            List<ProvinceConstant> provinceConstants = provinceConstantService.findConstants();            for (ProvinceConstant provinceConstant : provinceConstants) {                logger.error(provinceConstant.getConstantKey() + ":" + provinceConstant.getConstantValue());                map.put(provinceConstant.getConstantKey(), provinceConstant.getConstantValue());            }        }    }}