package cn.com.evo.integration.common.request;/** * @Description: 鉴权请求对象 * @author: lu.xin * @create: 2019-04-04 3:13 PM **/public class AuthRequest {    private String cardNo;    private String appId;    private String columnId;    private String contentId;    private String childContentId;    public static AuthRequest playAuthInit(String cardNo,                                           String appId, String columnId, String contentId,                                           String childContentId) {        AuthRequest request = new AuthRequest();        request.setCardNo(cardNo);        request.setAppId(appId);        request.setColumnId(columnId);        request.setContentId(contentId);        request.setChildContentId(childContentId);        return request;    }    public String getCardNo() {        return cardNo;    }    public void setCardNo(String cardNo) {        this.cardNo = cardNo;    }    public String getAppId() {        return appId;    }    public void setAppId(String appId) {        this.appId = appId;    }    public String getColumnId() {        return columnId;    }    public void setColumnId(String columnId) {        this.columnId = columnId;    }    public String getContentId() {        return contentId;    }    public void setContentId(String contentId) {        this.contentId = contentId;    }    public String getChildContentId() {        return childContentId;    }    public void setChildContentId(String childContentId) {        this.childContentId = childContentId;    }}