package cn.com.evo.integration.common.result;import cn.com.evo.cms.domain.entity.cms.CatalogueRelation;import cn.com.evo.cms.domain.entity.cms.Content;import lombok.Data;import lombok.EqualsAndHashCode;/** * @Description: * @author: lu.xin * @create: 2019-06-11 5:39 PM **/@Data@EqualsAndHashCode(callSuper = false)public class AuthContentResponse {    /**     * 内容id(关系id)     */    private String id;    /**     * 内容id     */    private String contentId;    /**     * 内容名称     */    private String contentName;    /**     * 简介     */    private String info;    /**     * 内容类型：1-电影；2-剧集     */    private Integer classify;    /**     * 总集数     */    private Integer count;    /**     * 子集名称     */    private String childName;    /**     * 当前集数     */    private Integer sort;    public AuthContentResponse(CatalogueRelation relation, Content content, Content child) {        this.id = relation.getId();        this.contentId = content.getId();        this.contentName = content.getName();        this.info = content.getInfo();        this.classify = content.getClassify();        this.count = content.getSumNum() == null ? 1 : content.getSumNum();        this.sort = child.getSort();        this.childName = child.getName();    }    public AuthContentResponse(CatalogueRelation relation, Content content) {        this.id = relation.getId();        this.contentId = content.getId();        this.contentName = content.getName();        this.info = content.getInfo();        this.classify = content.getClassify();        this.count = content.getSumNum() == null ? 1 : content.getSumNum();        this.sort = 1;        this.childName = content.getName();    }}