package cn.com.evo.integration.common.result;import cn.com.evo.cms.web.api.vo.VideoApiVo;import cn.com.evo.integration.common.dto.ProdDto;import cn.com.evo.integration.common.enums.StatusEnum;import com.google.common.collect.Lists;import com.google.common.collect.Maps;import lombok.Data;import lombok.EqualsAndHashCode;import java.util.List;import java.util.Map;/** * @Description: 鉴权返回消息 * @author: lu.xin * @create: 2019-04-03 6:07 PM **/@Data@EqualsAndHashCode(callSuper = false)public class AuthResponse {    private Integer retCode;    private String retMsg;    private AuthContentResponse content;    private List<VideoApiVo> videos = Lists.newArrayList();    private List<ProdDto> prods = Lists.newArrayList();    private Map<String, Object> extra = Maps.newHashMap();    public AuthResponse() {    }    public void pushError1(String retMsg) {        this.retCode = StatusEnum.error1.getRetCode();        this.retMsg = retMsg;    }    public void pushError2(String retMsg) {        this.retCode = StatusEnum.error2.getRetCode();        this.retMsg = retMsg;    }    public void pushError3(String retMsg) {        this.retCode = StatusEnum.error3.getRetCode();        this.retMsg = retMsg;    }    public void pushError4(String retMsg) {        this.retCode = StatusEnum.error4.getRetCode();        this.retMsg = retMsg;    }    public void pushError5(String retMsg) {        this.retCode = StatusEnum.error5.getRetCode();        this.retMsg = retMsg;    }    public void pushUserAuthFail() {        this.retCode = StatusEnum.user_auth_fail.getRetCode();        this.retMsg = StatusEnum.user_auth_fail.getRetMsg();    }    public void pushAuthSuccess(List<VideoApiVo> videos) {        this.retCode = StatusEnum.auth_success.getRetCode();        this.retMsg = StatusEnum.auth_success.getRetMsg();        this.videos = videos;    }    public void pushLimitFreeSuccess(List<VideoApiVo> videos) {        this.retCode = StatusEnum.limit_free_success.getRetCode();        this.retMsg = StatusEnum.limit_free_success.getRetMsg();        this.videos = videos;    }    public void pushServerAuthFail(List<VideoApiVo> videos) {        this.retCode = StatusEnum.server_auth_fail.getRetCode();        this.retMsg = StatusEnum.server_auth_fail.getRetMsg();        this.videos = videos;    }    public void pushOrderAuthFail(String productId, List<VideoApiVo> videos) {        this.retCode = StatusEnum.order_auth_fail.getRetCode();        this.retMsg = StatusEnum.order_auth_fail.getRetMsg();        this.videos = videos;        extra.put("productId", productId);    }    public void pushGoToPay(String payUrl, List<VideoApiVo> videos) {        this.retCode = StatusEnum.go_to_pay.getRetCode();        this.retMsg = StatusEnum.go_to_pay.getRetMsg();        this.videos = videos;        extra.put("payUrl", payUrl);    }}