package cn.com.evo.integration.guangxi.content.dto;import cn.com.evo.integration.common.ConstantFactory;import cn.com.evo.integration.guangxi.common.ConstantEnum;import com.alibaba.fastjson.JSONObject;import lombok.Data;import lombok.EqualsAndHashCode;/** * @Description: 分集删除接口请求对象 * @author: lu.xin * @create: 2019-05-15 1:52 PM **/@Data@EqualsAndHashCode(callSuper = false)public class ChildDeleteRequest {    /**     * 接口     */    private String func = "delete_video_clip";    /**     * 媒资类型 0：点播；1：直播     */    private String assetsVideoType = "0";    /**     * 分集id     */    private String id;    /**     * 分集id类型     */    private int indexIdType = 1;    /**     * 主媒资id     */    private String assetsId;    /**     * 主媒资id类型     */    private int assetsIdType = 1;    /**     * cpId     */    private String cpId = ConstantFactory.map.get(ConstantEnum.cp_id.getKey());    /**     * 是否真实删除。1是（CP/SP方对接请使用该值）；0否     */    private int realDelete = 1;    /**     * 分集删除     *     * @param assets_id 主媒资id     * @param id        分集id     */    public ChildDeleteRequest(String assets_id, String id) {        this.assetsId = assets_id;        this.id = id;    }    /**     * 转换为json对象     *     * @return     */    public JSONObject toJson() {        JSONObject jsonObject = new JSONObject();        jsonObject.put("func", func);        jsonObject.put("assets_id", assetsId);        jsonObject.put("assets_Id_Type", assetsIdType);        jsonObject.put("id", id);        jsonObject.put("index_Id_Type", indexIdType);        jsonObject.put("cp_id", cpId);        jsonObject.put("assets_video_type", assetsVideoType);        jsonObject.put("real_delete", realDelete);        return jsonObject;    }}