package cn.com.evo.integration.guangxi.content.dto;import cn.com.evo.integration.common.ConstantFactory;import cn.com.evo.integration.guangxi.common.ConstantEnum;import com.alibaba.fastjson.JSONObject;import lombok.Data;import lombok.EqualsAndHashCode;/** * @Description: 主媒资上下线接口请求对象 * @author: lu.xin * @create: 2019-05-15 1:52 PM **/@Data@EqualsAndHashCode(callSuper = false)public class MainUnlineRequest {    /**     * 接口     */    private String func = "unline_assets";    /**     * 主媒资id     */    private String id;    /**     * 主媒资id类型     */    private int assetsIdType = 1;    /**     * cpId     */    private String cpId = ConstantFactory.map.get(ConstantEnum.cp_id.getKey());    /**     * 0:上线     * 1:下线     */    private int assetsUnline;    /**     * 主媒资上下线     *     * @param id           媒资id     * @param assetsUnline 0:上线     *                     1:下线     */    public MainUnlineRequest(String id, int assetsUnline) {        this.id = id;        this.assetsUnline = assetsUnline;    }    /**     * 转换为json对象     *     * @return     */    public JSONObject toJson() {        JSONObject jsonObject = new JSONObject();        jsonObject.put("func", this.func);        jsonObject.put("id", this.id);        jsonObject.put("assets_Id_Type", this.assetsIdType);        jsonObject.put("cp_id", this.cpId);        jsonObject.put("assets_unline", this.assetsUnline);        return jsonObject;    }}