package cn.com.evo.integration.guangxi.content.dto;import cn.com.evo.integration.common.ConstantFactory;import cn.com.evo.integration.guangxi.common.ConstantEnum;import com.alibaba.fastjson.JSONObject;import lombok.Data;import lombok.EqualsAndHashCode;/** * @Description: 片源上下线接口请求对象 * @author: lu.xin * @create: 2019-05-15 1:52 PM **/@Data@EqualsAndHashCode(callSuper = false)public class VideoDeleteRequest {    /**     * 接口     */    private String func = "delete_video_file";    private String assetsVideoType = "0";    /**     * 片源id     */    private String id;    /**     * 主媒资id类型     */    private int assetsIdType = 1;    /**     * cpId     */    private String cpId = ConstantFactory.map.get(ConstantEnum.cp_id.getKey());    /**     * 片源删除     *     * @param id 媒资id     */    public VideoDeleteRequest(String id) {        this.id = id;    }    /**     * 转换为json对象     *     * @return     */    public JSONObject toJson() {        JSONObject jsonObject = new JSONObject();        jsonObject.put("func", this.func);        jsonObject.put("assets_video_type", this.assetsVideoType);        jsonObject.put("id", this.id);        jsonObject.put("assets_Id_Type", this.assetsIdType);        jsonObject.put("cp_id", this.cpId);        return jsonObject;    }}