package cn.com.evo.integration.guangxi.content.dto;import cn.com.evo.integration.common.ConstantFactory;import cn.com.evo.integration.guangxi.common.ConstantEnum;import com.alibaba.fastjson.JSONObject;import lombok.Data;import lombok.EqualsAndHashCode;/** * @Description: 片源上下线接口请求对象 * @author: lu.xin * @create: 2019-05-15 1:52 PM **/@Data@EqualsAndHashCode(callSuper = false)public class VideoUnlineRequest {    /**     * 接口     */    private String func = "unline_video_file";    /**     * 片源id     */    private String id;    /**     * 主媒资id类型     */    private int assetsIdType = 1;    /**     * cpId     */    private String cpId = ConstantFactory.map.get(ConstantEnum.cp_id.getKey());    /**     * 0:上线     * 1:下线     */    private int assetsFileUnline;    /**     * 是否强制上下线     * 0：是     * 1：否     */    private int isForceUnline = 0;    /**     * 片源上下线     *     * @param id               媒资id     * @param assetsFileUnline 0:上线     *                         1:下线     */    public VideoUnlineRequest(String id, int assetsFileUnline) {        this.id = id;        this.assetsFileUnline = assetsFileUnline;    }    /**     * 转换为json对象     *     * @return     */    public JSONObject toJson() {        JSONObject jsonObject = new JSONObject();        jsonObject.put("func", this.func);        jsonObject.put("id", this.id);        jsonObject.put("assets_Id_Type", this.assetsIdType);        jsonObject.put("cp_id", this.cpId);        jsonObject.put("assets_file_unline", this.assetsFileUnline);        jsonObject.put("is_force_unline", this.isForceUnline);        return jsonObject;    }}