package cn.com.evo.integration.guangxi.content.xml;import cn.com.evo.cms.domain.entity.cms.Content;import cn.com.evo.cms.domain.entity.cms.Picture;import cn.com.evo.cms.domain.entity.cms.Video;import cn.com.evo.integration.common.utils.BeanToXml;import cn.com.evo.integration.guangxi.content.xml.model.*;import javax.xml.bind.JAXBException;import java.util.List;/** * @Description: * @author: lu.xin * @create: 2019-05-14 11:16 AM **/public class CreateXML {    /**     * 创建xml     *     * @return     */    public static String create(Content content, Content child, Video video, Picture hPicture, Picture sPicture) throws JAXBException {        AMS ams = AMS.createAMS("package", content.getId(), content.getName());        List<AppData> appDatas = AppData.createPackage(content);        Metadata metadata = Metadata.createMetadata(ams, appDatas);        Assets assets = Assets.create(content, child, video, hPicture, sPicture);        ADI adi = new ADI(metadata, assets);        String xmlStr = BeanToXml.beanToXml(adi, false);        return xmlStr;    }    public static void main(String[] args) {        try {            Content content = new Content();            Content child = new Content();            Video video = new Video();            Picture hPicture = new Picture();            Picture sPicture = new Picture();            content.setId("1");            content.setName("测试电影");            System.out.println(create(content, child, video, hPicture, sPicture));        } catch (JAXBException e) {            e.printStackTrace();        }    }}