package cn.com.evo.integration.guangxi.content.xml.model;import cn.com.evo.cms.domain.entity.cms.Content;import cn.com.evo.cms.domain.entity.cms.Video;import com.google.common.collect.Lists;import javax.xml.bind.annotation.XmlAttribute;import javax.xml.bind.annotation.XmlType;import java.util.List;/** * @Description: * @author: lu.xin * @create: 2019-05-14 11:21 AM **/@XmlType(propOrder = {        "app",        "name",        "value"})public class AppData {    private String app = "MOD";    private String name;    private String value;    public AppData() {    }    public AppData(String name, String value) {        this.name = name;        this.value = value;    }    public static List<AppData> createPackage(Content content) {        List<AppData> appData = Lists.newArrayList();        appData.add(new AppData("Licensing_Window_Start", ""));        appData.add(new AppData("VodResourceType", "1"));        appData.add(new AppData("Classify", "电视剧"));        appData.add(new AppData("Keyword", content.getName()));        appData.add(new AppData("Director", content.getDirectorTags()));        appData.add(new AppData("Actors", content.getActorTags()));        appData.add(new AppData("Country_Of_Origin", content.getAreaTags()));        appData.add(new AppData("Summary_Long", content.getInfo()));        appData.add(new AppData("Year", content.getYear()));        appData.add(new AppData("Language", content.getLanguage()));        appData.add(new AppData("TotalNumber", content.getSumNum() == null ? "1" : content.getSumNum() + ""));        appData.add(new AppData("Metadata_Spec_Version", "CableLabsVOD1.1"));        appData.add(new AppData("Genre", "正片"));        appData.add(new AppData("Licensing_Window_End", ""));        return appData;    }    public static List<AppData> createTitle(Content content, Content child) {        List<AppData> appData = Lists.newArrayList();        appData.add(new AppData("Title_Sec", ""));        appData.add(new AppData("Premiere_Date", ""));        appData.add(new AppData("Suggested_Price", ""));        appData.add(new AppData("Writers", ""));        appData.add(new AppData("Show_Type", "电视剧"));        appData.add(new AppData("Summary_Long", content.getInfo()));        appData.add(new AppData("Language", "普通话"));        appData.add(new AppData("Column", ""));        appData.add(new AppData("Provider_Sec", ""));        appData.add(new AppData("Writers_Sec", ""));        appData.add(new AppData("TotalNumber", content.getSumNum() == null ? "1" : content.getSumNum() + ""));        appData.add(new AppData("Season", "1"));        appData.add(new AppData("Licensing_Window_End", ""));        appData.add(new AppData("Subtitle_Language", ""));        appData.add(new AppData("HighLight", ""));        appData.add(new AppData("Licensing_Window_Start", ""));        appData.add(new AppData("Keyword", content.getName()));        appData.add(new AppData("Audience", ""));        appData.add(new AppData("Rate", ""));        appData.add(new AppData("Title_Brief", ""));        appData.add(new AppData("Chapter", child.getSort() == null ? "1" : child.getSort() + ""));        appData.add(new AppData("Director", content.getDirectorTags()));        appData.add(new AppData("Title", content.getName()));        appData.add(new AppData("Actors", content.getActorTags()));        appData.add(new AppData("Play_Date", ""));        appData.add(new AppData("Director_Sec", ""));        appData.add(new AppData("Country_Of_Origin", content.getAreaTags()));        appData.add(new AppData("Summary_Long_Sec", ""));        appData.add(new AppData("Provider", ""));        appData.add(new AppData("Run_Time", child.getRunTime() == null ? "100" : child.getRunTime()));        appData.add(new AppData("AssetType", ""));        appData.add(new AppData("Actors_Sec", ""));        appData.add(new AppData("Awards", ""));        appData.add(new AppData("Year", content.getYear()));        appData.add(new AppData("Country_Of_Origin_Sec", ""));        appData.add(new AppData("Genre", ""));        appData.add(new AppData("Screen_Writer", ""));        return appData;    }    public static List<AppData> createMovie(Video video) {        List<AppData> appData = Lists.newArrayList();        appData.add(new AppData("Run_Time", video.getTime()));        appData.add(new AppData("CodeFormat", "MPEG-TS"));        appData.add(new AppData("SD_Format", ""));        appData.add(new AppData("Format", "高清"));        appData.add(new AppData("Content_FileSize", ""));        appData.add(new AppData("Asset_Tag", ""));        appData.add(new AppData("Asset_Profile", ""));        appData.add(new AppData("FileFormat", "MPEG-TS"));        appData.add(new AppData("Bit_Rate", ""));        appData.add(new AppData("HD_Format", ""));        appData.add(new AppData("Strean_Size", ""));        return appData;    }    public static List<AppData> createPoster0() {        List<AppData> appData = Lists.newArrayList();        appData.add(new AppData("Ui_Style", "0"));        appData.add(new AppData("Title", ""));        appData.add(new AppData("Summary", ""));        return appData;    }    public static List<AppData> createPoster1() {        List<AppData> appData = Lists.newArrayList();        appData.add(new AppData("Ui_Style", "1"));        appData.add(new AppData("Title", ""));        appData.add(new AppData("Summary", ""));        return appData;    }    @XmlAttribute(name = "App")    public String getApp() {        return app;    }    @XmlAttribute(name = "Name")    public String getName() {        return name;    }    @XmlAttribute(name = "Value")    public String getValue() {        return value;    }    public void setApp(String app) {        this.app = app;    }    public void setName(String name) {        this.name = name;    }    public void setValue(String value) {        this.value = value;    }}