package cn.com.evo.integration.guangxi.content.xml.model;import cn.com.evo.cms.domain.entity.cms.Content;import cn.com.evo.cms.domain.entity.cms.Picture;import cn.com.evo.cms.domain.entity.cms.Video;import javax.xml.bind.annotation.XmlElement;import javax.xml.bind.annotation.XmlType;import java.util.List;/** * @Description: * @author: lu.xin * @create: 2019-05-14 2:02 PM **/@XmlType(propOrder = {        "metadata",        "asset",})public class Assets {    private Metadata metadata;    private List<Asset> asset;    public static Assets create(Content content, Content child, Video video, Picture hPicture, Picture sPicture) {        Assets assets = new Assets();        AMS ams = AMS.createAMS("title", child.getId(), content.getName());        List<AppData> appDatas = AppData.createTitle(content, child);        Metadata metadata = new Metadata(ams, appDatas);        assets.setMetadata(metadata);        List<Asset> assetList = Asset.create(video, content, hPicture, sPicture);        assets.setAsset(assetList);        return assets;    }    @XmlElement(name = "Metadata")    public Metadata getMetadata() {        return metadata;    }    @XmlElement(name = "Asset")    public List<Asset> getAsset() {        return asset;    }    public void setMetadata(Metadata metadata) {        this.metadata = metadata;    }    public void setAsset(List<Asset> asset) {        this.asset = asset;    }}