package cn.com.evo.integration.guangxi.pay.dto.bind;import cn.com.evo.cms.domain.entity.cms.Content;import cn.com.evo.integration.common.ConstantFactory;import cn.com.evo.integration.guangxi.common.ConstantEnum;import com.google.common.collect.Lists;import lombok.Data;import lombok.EqualsAndHashCode;import java.util.List;/** * @Description: * @author: lu.xin * @create: 2019-05-15 4:21 PM **/@Data@EqualsAndHashCode(callSuper = false)public class BindRemoveContent {    private String product_id = ConstantFactory.map.get(ConstantEnum.product_id.getKey());    private String asset_id;    public BindRemoveContent(String asset_id) {        this.asset_id = asset_id;    }    /**     * 初始化对象     *     * @param contents     * @return     */    public static List<BindRemoveContent> init(List<Content> contents) {        List<BindRemoveContent> bindAddContents = Lists.newArrayList();        for (Content content : contents) {            BindRemoveContent bindAddContent = new BindRemoveContent(content.getId());            bindAddContents.add(bindAddContent);        }        return bindAddContents;    }}