package cn.com.evo.integration.guangxi.pay.dto.pay;import lombok.Data;import lombok.EqualsAndHashCode;/** * @Description: 封装产品信息 * @author: lu.xin * @create: 2019-05-21 3:43 PM **/@Data@EqualsAndHashCode(callSuper = false)public class ProductInfo {    /**     * 产品id     */    private String productId;    /**     * 产品名称     */    private String productName;    /**     * 资费id     */    private String tariffId;    /**     * 产品单价     */    private String unitPrice;    public ProductInfo(String productId, String productName, String tariffId, String unitPrice) {        this.productId = productId;        this.productName = productName;        this.tariffId = tariffId;        this.unitPrice = unitPrice;    }}