package cn.com.evo.integration.guangxi.pay.dto.pay.dto.order;import cn.com.evo.integration.common.ConstantFactory;import cn.com.evo.integration.guangxi.common.ConstantEnum;import com.frameworks.utils.SignUtil;import com.google.common.collect.Maps;import javax.xml.bind.annotation.XmlElement;import javax.xml.bind.annotation.XmlType;import java.util.Map;/** * @Description: 业务开通订单创建接口请求参数 * @author: lu.xin * @create: 2019-05-21 3:50 PM **/@XmlType(propOrder = {        "method",        "productId",        "productName",        "tariffId",        "qty",        "userId",        "stbId",        "areaCode",        "partner",        "callbackUrl",        "noticeUrl",        "appIndexUrl",        "unitPrice",        "isHD",})public class OrderProductRequest {    private String method = "prodOrder";    /**     * 产品id     */    private String productId;    /**     * 产品名称     */    private String productName;    /**     * 资费id     */    private String tariffId;    /**     * 开通数量     */    private String qty;    /**     * 用户id     */    private String userId;    /**     * 机顶盒号     */    private String stbId;    /**     * 用户区域码     */    private String areaCode;    /**     * 商户号     */    private String partner = ConstantFactory.map.get(ConstantEnum.partner.getKey());    /**     * 回调url     */    private String callbackUrl = ConstantFactory.map.get(ConstantEnum.callbackUrl.getKey());    /**     * 异步通知url     */    private String noticeUrl = ConstantFactory.map.get(ConstantEnum.noticeUrl.getKey());    ;    /**     * 应用首页地址     */    private String appIndexUrl = ConstantFactory.map.get(ConstantEnum.appIndexUrl.getKey());    ;    /**     * 产品单价     */    private String unitPrice;    /**     * 机顶盒类型     * "720P"高清,"480P"标清     */    private String isHD;    /**     * 签名     */    private String sign;    public OrderProductRequest() {    }    /**     * @param productId   产品id     * @param productName 产品名称     * @param tariffId    资费id     * @param qty         订购周期     * @param userId      用户id     * @param stbId       机顶盒号     * @param areaCode    用户区域码     * @param unitPrice   单价     * @param isHD        机顶盒类型 "720P"高清,"480P"标清     */    public OrderProductRequest(String productId, String productName, String tariffId, String qty, String userId,                               String stbId, String areaCode, String unitPrice, String isHD) {        this.productId = productId;        this.productName = productName;        this.tariffId = tariffId;        this.qty = qty;        this.userId = userId;        this.stbId = stbId;        this.areaCode = areaCode;        this.unitPrice = unitPrice;        this.isHD = isHD;        String payKey = ConstantFactory.map.get(ConstantEnum.pay_key.getKey());        this.sign = SignUtil.buildSign(toMap(), payKey);    }    public Map<String, String> toMap() {        Map<String, String> map = Maps.newHashMap();        map.put("method", this.method);        map.put("productId", this.productId);        map.put("productName", this.productName);        map.put("tariffId", this.tariffId);        map.put("qty", this.qty);        map.put("userId", this.userId);        map.put("stbId", this.stbId);        map.put("areaCode", this.areaCode);        map.put("partner", this.partner);        map.put("callbackUrl", this.callbackUrl);        map.put("noticeUrl", this.noticeUrl);        map.put("appIndexUrl", this.appIndexUrl);        map.put("isHD", this.isHD);        return map;    }    public String toParams() {        String params = "method=" + this.method + "&productId=" + this.productId +                "&productName=" + this.productName + "&tariffId=" + this.tariffId +                "&qty=" + this.qty + "&userId=" + this.userId +                "&stbId=" + this.stbId + "&areaCode=" + this.areaCode +                "&partner=" + this.partner + "&callbackUrl=" + this.callbackUrl +                "&noticeUrl=" + this.noticeUrl + "&appIndexUrl=" + this.appIndexUrl +                "&isHD=" + this.isHD + "&sign=" + this.sign;        return params;    }    @XmlElement(name = "method")    public String getMethod() {        return method;    }    @XmlElement(name = "productId")    public String getProductId() {        return productId;    }    @XmlElement(name = "productName")    public String getProductName() {        return productName;    }    @XmlElement(name = "tariffId")    public String getTariffId() {        return tariffId;    }    @XmlElement(name = "qty")    public String getQty() {        return qty;    }    @XmlElement(name = "userId")    public String getUserId() {        return userId;    }    @XmlElement(name = "stbId")    public String getStbId() {        return stbId;    }    @XmlElement(name = "areaCode")    public String getAreaCode() {        return areaCode;    }    @XmlElement(name = "partner")    public String getPartner() {        return partner;    }    @XmlElement(name = "callbackUrl")    public String getCallbackUrl() {        return callbackUrl;    }    @XmlElement(name = "noticeUrl")    public String getNoticeUrl() {        return noticeUrl;    }    @XmlElement(name = "appIndexUrl")    public String getAppIndexUrl() {        return appIndexUrl;    }    @XmlElement(name = "unitPrice")    public String getUnitPrice() {        return unitPrice;    }    @XmlElement(name = "isHD")    public String getIsHD() {        return isHD;    }    public String getSign() {        return sign;    }    public void setMethod(String method) {        this.method = method;    }    public void setProductId(String productId) {        this.productId = productId;    }    public void setProductName(String productName) {        this.productName = productName;    }    public void setTariffId(String tariffId) {        this.tariffId = tariffId;    }    public void setQty(String qty) {        this.qty = qty;    }    public void setUserId(String userId) {        this.userId = userId;    }    public void setStbId(String stbId) {        this.stbId = stbId;    }    public void setAreaCode(String areaCode) {        this.areaCode = areaCode;    }    public void setPartner(String partner) {        this.partner = partner;    }    public void setCallbackUrl(String callbackUrl) {        this.callbackUrl = callbackUrl;    }    public void setNoticeUrl(String noticeUrl) {        this.noticeUrl = noticeUrl;    }    public void setAppIndexUrl(String appIndexUrl) {        this.appIndexUrl = appIndexUrl;    }    public void setUnitPrice(String unitPrice) {        this.unitPrice = unitPrice;    }    public void setIsHD(String isHD) {        this.isHD = isHD;    }    public void setSign(String sign) {        this.sign = sign;    }}