package cn.com.evo.integration.jinanyouxian.sdk.dto;import java.util.List;/** * @Description: 获取套餐详情接口返回值封装 * @author: lu.xin * @create: 2019-04-16 6:19 PM **/public class GetPackageInfoResponse {    /**     * 返回码，返回 0 表示成功，返回其他值表示失败。     */    private Integer ret;    /**     * 返回码对应的描述信息     */    private String ret_Msg;    /**     * 套餐名称     */    private String name;    /**     * 套餐类型，取值 1:直播，2:点播，3:回看，4: 应用，5:时移，6:综合     */    private Integer type;    /**     * 套餐的扩展属性     */    private Integer extend;    /**     * 是否可续订，取值 1:是，0:否。     */    private Integer renewable;    /**     * 套餐绑定限制，取值 0:无限制，1:绑定家庭，2: 绑定机顶盒，3:绑定用户     */    private Integer binding_restrict;    /**     * 业务简介     */    private String desc;    /**     * 套餐包含的节目     */    private List<Program> programs;    /**     * 套餐价格     */    private List price;    /**     * 使用时长     */    private UsableDuration usable_duration;    /**     * 套餐可订购的有效期     */    private ValidDuration available_duration;    public Integer getRet() {        return ret;    }    public void setRet(Integer ret) {        this.ret = ret;    }    public String getRet_Msg() {        return ret_Msg;    }    public void setRet_Msg(String ret_Msg) {        this.ret_Msg = ret_Msg;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getType() {        return type;    }    public void setType(Integer type) {        this.type = type;    }    public Integer getExtend() {        return extend;    }    public void setExtend(Integer extend) {        this.extend = extend;    }    public Integer getRenewable() {        return renewable;    }    public void setRenewable(Integer renewable) {        this.renewable = renewable;    }    public Integer getBinding_restrict() {        return binding_restrict;    }    public void setBinding_restrict(Integer binding_restrict) {        this.binding_restrict = binding_restrict;    }    public String getDesc() {        return desc;    }    public void setDesc(String desc) {        this.desc = desc;    }    public List<Program> getPrograms() {        return programs;    }    public void setPrograms(List<Program> programs) {        this.programs = programs;    }    public List getPrice() {        return price;    }    public void setPrice(List price) {        this.price = price;    }    public UsableDuration getUsable_duration() {        return usable_duration;    }    public void setUsable_duration(UsableDuration usable_duration) {        this.usable_duration = usable_duration;    }    public ValidDuration getAvailable_duration() {        return available_duration;    }    public void setAvailable_duration(ValidDuration available_duration) {        this.available_duration = available_duration;    }    public class Program {        /**         * 节目 id         */        private Integer id;        /**         * 节目名称         */        private String name;        /**         * 节目的类型，取值 1:直播，2:点播，3:回看，4: 应用，5:时移         */        private Integer type;        public Integer getId() {            return id;        }        public void setId(Integer id) {            this.id = id;        }        public String getName() {            return name;        }        public void setName(String name) {            this.name = name;        }        public Integer getType() {            return type;        }        public void setType(Integer type) {            this.type = type;        }    }    public class Price {        /**         * 价格对应的货币种类，取值 1:人民币。10000 以上: 虚拟资产，取值为虚拟资产的 id。         */        private Integer currency;        /**         * 价格取值，人民币的单位为分，虚拟资产为最小单位 个数         */        private Integer amount;        /**         * 价格有效期，即可购买时间，生效了返回给终端         */        private ValidDuration valid_duration;        /**         * 虚拟资产信息         */        private VassetInfo vasset_info;        public Integer getCurrency() {            return currency;        }        public void setCurrency(Integer currency) {            this.currency = currency;        }        public Integer getAmount() {            return amount;        }        public void setAmount(Integer amount) {            this.amount = amount;        }        public ValidDuration getValid_duration() {            return valid_duration;        }        public void setValid_duration(ValidDuration valid_duration) {            this.valid_duration = valid_duration;        }        public VassetInfo getVasset_info() {            return vasset_info;        }        public void setVasset_info(VassetInfo vasset_info) {            this.vasset_info = vasset_info;        }    }    /**     * starttime 与 endtime 都取值 0 表示一直有效。     */    public class ValidDuration {        /**         * 有效期开始时间，utc 时间，单位秒         */        private Long start_time;        /**         * 有效期结束时间，utc 时间，单位秒         */        private Long end_time;        public Long getStart_time() {            return start_time;        }        public void setStart_time(Long start_time) {            this.start_time = start_time;        }        public Long getEnd_time() {            return end_time;        }        public void setEnd_time(Long end_time) {            this.end_time = end_time;        }    }    public class VassetInfo {        /**         * 虚拟资产名称         */        private String vasset_name;        /**         * 虚拟资产的购买价格，币种为人民币         */        private Integer vasset_price;        /**         * 价格可购买的虚拟资产的个数         */        private Integer vasset_amount;        /**         * 虚拟资产描述         */        private String vasset_desc;        /**         * 使用时长         */        private UsableDuration usable_duration;        public String getVasset_name() {            return vasset_name;        }        public void setVasset_name(String vasset_name) {            this.vasset_name = vasset_name;        }        public Integer getVasset_price() {            return vasset_price;        }        public void setVasset_price(Integer vasset_price) {            this.vasset_price = vasset_price;        }        public Integer getVasset_amount() {            return vasset_amount;        }        public void setVasset_amount(Integer vasset_amount) {            this.vasset_amount = vasset_amount;        }        public String getVasset_desc() {            return vasset_desc;        }        public void setVasset_desc(String vasset_desc) {            this.vasset_desc = vasset_desc;        }        public UsableDuration getUsable_duration() {            return usable_duration;        }        public void setUsable_duration(UsableDuration usable_duration) {            this.usable_duration = usable_duration;        }    }    public class UsableDuration {        /**         * 使用时长取值         */        private Integer value;        /**         * 使用时长的单位，取值 1:次，2:天，3:月，4: 年         */        private Integer unit;        public Integer getValue() {            return value;        }        public void setValue(Integer value) {            this.value = value;        }        public Integer getUnit() {            return unit;        }        public void setUnit(Integer unit) {            this.unit = unit;        }    }}