package cn.com.evo.integration.qinghai.sdk.request;import cn.com.evo.integration.common.ConstantFactory;import cn.com.evo.integration.qinghai.common.ConstantEnum;import com.alibaba.fastjson.JSONObject;/** * @Description: 青海媒资注入类接口请求参数封装 * @author: lu.xin * @create: 2019-06-25 2:27 PM **/@Deprecatedpublic class QingHaiContentRequest {    /**     * 媒资id     */    private String assetId;    /**     * 媒资名称     */    private String assetName;    /**     * 媒资类型 0-剧集；1-电影     */    private String type;    /**     * 所属运营商id     */    private String providerId = ConstantFactory.map.get(ConstantEnum.provider_id.getKey());    /**     * 剧集 id(如果为电影此参数与 assetId 相同，如果是 剧集，则为子集 id)     */    private String episodeId;    /**     * 子集名，如果为电影，此参数与 assetName 相同, 如果为剧集格式为 name_chapter     */    private String episodeName;    /**     * 所属产品包     */    private String productCode = ConstantFactory.map.get(ConstantEnum.product_code.getKey());    /**     * 媒资注入的服务器文件夹     */    private String folder;    public QingHaiContentRequest(String assetId, String assetName, String type, String episodeId, String episodeName, String folder) {        this.assetId = assetId;        this.assetName = assetName;        this.type = type;        this.episodeId = episodeId;        this.episodeName = episodeName;        this.folder = folder;    }    /**     * 转换为请求参数     *     * @return     */    public JSONObject toParams() {        JSONObject json = new JSONObject();        json.put("assetId", this.assetId);        json.put("assetName", this.assetName);        json.put("type", this.type);        json.put("episodeId", this.episodeId);        json.put("episodeName", this.episodeName);        json.put("productCode", this.productCode);        json.put("providerId", this.providerId);        json.put("folder", this.folder);        return json;    }}