package cn.com.evo.integration.qinghai.sdk.request;import cn.com.evo.integration.common.ConstantFactory;import cn.com.evo.integration.qinghai.common.ConstantEnum;import lombok.Data;import lombok.EqualsAndHashCode;/** * @Description: 获取栏目下内容接口请求参数封装 * @author: lu.xin * @create: 2019-06-25 9:51 AM **/@Data@EqualsAndHashCode(callSuper = false)public class QingHaiFindContentsRequest {    private String searchType = "vods";    private String equipType = "base";    private String columnId = ConstantFactory.map.get(ConstantEnum.lm_id.getKey());    private String siteId = ConstantFactory.map.get(ConstantEnum.site_id.getKey());    private String pageSize;    private String pageNum;    public QingHaiFindContentsRequest(String columnId, String pageSize, String pageNum) {        this.columnId = columnId;        this.pageSize = pageSize;        this.pageNum = pageNum;    }    public String toParams() {        String params = "searchType=" + this.searchType + "&siteId=" + this.siteId +                "&lmId=" + this.columnId + "&equipType=" + equipType +                "$pageSize=" + this.pageSize + "&pageYema=" + this.pageNum;        return params;    }}