package cn.com.evo.integration.qinghai.sdk.response;import com.alibaba.fastjson.JSONArray;import com.alibaba.fastjson.annotation.JSONField;import lombok.Data;import lombok.EqualsAndHashCode;import java.util.List;/** * @Description: 获取栏目下子栏目接口返回结果封装 * @author: lu.xin * @create: 2019-06-25 9:51 AM **/@Data@EqualsAndHashCode(callSuper = false)public class QingHaiFindChildColumnResponse {    @JSONField(name = "lmId")    private String columnId;    private String type;    @JSONField(name = "lmName")    private String name;    public static void main(String[] args) {        String resultStr = "[{\n" +                "\"lmId\":\"qhyszb\",\n" +                "\"type\":\"zb\",\n" +                "\"lmName\":\"直播\"\n" +                "},\n" +                "{\n" +                "\"lmId\":\"db\",\n" +                "\"type\":\"vod\",\n" +                "\"lmName\":\"点播\"\n" +                "}]";        List<QingHaiFindChildColumnResponse> list = JSONArray.parseArray(resultStr, QingHaiFindChildColumnResponse.class);        for (QingHaiFindChildColumnResponse response : list) {            System.out.println(response.getColumnId());            System.out.println(response.getType());            System.out.println(response.getName());        }    }}