package cn.com.evo.integration.scyd.common;import cn.com.evo.integration.common.request.AuthRequest;/** * @Description: * @author: lu.xin * @create: 2019-04-09 2:11 PM **/public class ScydAuthRequest extends AuthRequest {    public static ScydAuthRequest playAuthInit(String cardNo,                                               String appId, String columnId, String contentId,                                               String childContentId) {        ScydAuthRequest request = new ScydAuthRequest();        request.setCardNo(cardNo);        request.setAppId(appId);        request.setColumnId(columnId);        request.setContentId(contentId);        request.setChildContentId(childContentId);        return request;    }}