package cn.com.evo.integration.scyd.content.xml;import cn.com.evo.cms.domain.entity.cms.Content;import java.util.List;/** * @Description: * @author: lu.xin * @create: 2019-03-14 2:42 PM **/public class ContentDto {    private Content content;    private List<String> pictureUrls;    private List<ChildContentDto> children;    public Content getContent() {        return content;    }    public void setContent(Content content) {        this.content = content;    }    public List<ChildContentDto> getChildren() {        return children;    }    public void setChildren(List<ChildContentDto> children) {        this.children = children;    }    public List<String> getPictureUrls() {        return pictureUrls;    }    public void setPictureUrls(List<String> pictureUrls) {        this.pictureUrls = pictureUrls;    }}