package cn.com.evo.integration.scyd.content.xml.model;import javax.xml.bind.annotation.XmlAttribute;import javax.xml.bind.annotation.XmlType;import java.util.UUID;/** * @Description: * @author: lu.xin * @create: 2019-03-05 4:09 PM **/@XmlType(propOrder = {        "id",        "action",        "parentType",        "parentId",        "parentCode",        "elementType",        "elementId",        "elementCode",})public class Mapping {    /**     * 全局唯一id     */    private String id;    /**     * 动作     * REGIST 注册     * UPDATE 修改     * DELETE 删除     */    private String action;    /**     * 父类型     */    private String parentType;    /**     * 父id     */    private String parentId;    /**     * 父code     */    private String parentCode;    /**     * 子类类型     */    private String elementType;    /**     * 子类id     */    private String elementId;    /**     * 子类code     */    private String elementCode;    public Mapping(String action, String parentType, String parentId, String parentCode,                   String elementType, String elementId, String elementCode) {        this.id = UUID.randomUUID().toString().replaceAll("-", "");        this.action = action;        this.parentType = parentType;        this.parentId = parentId;        this.parentCode = parentCode;        this.elementType = elementType;        this.elementId = elementId;        this.elementCode = elementCode;    }    public Mapping() {    }    @XmlAttribute(name = "Action")    public String getAction() {        return action;    }    @XmlAttribute(name = "ID")    public String getId() {        return id;    }    @XmlAttribute(name = "ParentType")    public String getParentType() {        return parentType;    }    @XmlAttribute(name = "ParentID")    public String getParentId() {        return parentId;    }    @XmlAttribute(name = "ParentCode")    public String getParentCode() {        return parentCode;    }    @XmlAttribute(name = "ElementType")    public String getElementType() {        return elementType;    }    @XmlAttribute(name = "ElementID")    public String getElementId() {        return elementId;    }    @XmlAttribute(name = "ElementCode")    public String getElementCode() {        return elementCode;    }    public void setAction(String action) {        action = action;    }    public void setId(String id) {        this.id = id;    }    public void setParentType(String parentType) {        this.parentType = parentType;    }    public void setParentId(String parentId) {        this.parentId = parentId;    }    public void setParentCode(String parentCode) {        this.parentCode = parentCode;    }    public void setElementType(String elementType) {        this.elementType = elementType;    }    public void setElementId(String elementId) {        this.elementId = elementId;    }    public void setElementCode(String elementCode) {        this.elementCode = elementCode;    }}