package cn.com.evo.integration.scyd.content.xml.model;import cn.com.evo.cms.domain.entity.cms.Content;import cn.com.evo.cms.domain.entity.cms.Video;import cn.com.evo.integration.scyd.common.ScydUtils;import cn.com.evo.integration.scyd.content.xml.enums.ElementTypeEnum;import javax.xml.bind.annotation.XmlAttribute;import javax.xml.bind.annotation.XmlElement;import javax.xml.bind.annotation.XmlType;import java.util.List;/** * @Description: * @author: lu.xin * @create: 2019-03-05 4:21 PM **/@XmlType(propOrder = {        "elementType",        "id",        "action",        "code",        "property",})public class Object {    /**     * 元素类型     * Program     * Movie     * Series     */    private String elementType;    /**     * 全局唯一标识     */    private String id;    /**     * 操作类型     * REGIST 新增     * UPDATE 修改     * DELETE 删除     */    private String action = "REGIST";    /**     * 全局唯一标识     */    private String code;    /**     * 属性     */    private List<Property> property;    /**     * 创建-剧集-Object对象     *     * @param content     内容对象     * @param pictureUrls 图片地址list     * @return     */    public static Object createSeriesObject(Content content, List<String> pictureUrls, String action) {        List<Property> properties = Property.createSeriesProperties(content, pictureUrls);        Object object = new Object(ElementTypeEnum.Series.getValue(), "s_" + content.getId(),                action, "s_" + content.getId(), properties);        return object;    }    /**     * 创建-节目-Object对象     *     * @param content     内容对象     * @param child       子内容对象     * @param pictureUrls 图片地址list     * @param volumnCount 当前集数     * @return     */    public static Object createProgramObject(Content content, Content child, List<String> pictureUrls, Integer volumnCount, String action) {        List<Property> properties = Property.createProgramProperties(content, child, pictureUrls, volumnCount);        Object object = new Object(ElementTypeEnum.Program.getValue(), "p_" + child.getId(),                action, "p_" + child.getId(), properties);        return object;    }    /**     * 创建-媒体-Object对象     *     * @param content 内容对象     * @param video   视频对象     * @param action  动作     * @return     */    public static Object createMovieObject(Content content, Video video, String action) {        List<Property> properties = Property.createMovieProperties("m_" + video.getId(),                ScydUtils.createVideoHttpUrl(video), video.getResolution(), content, video);        Object object = new Object(ElementTypeEnum.Movie.getValue(), "m_" + video.getId(),                action, "m_" + video.getId(), properties);        return object;    }    public Object() {    }    public Object(String elementType, String id, String action, String code, List<Property> property) {        this.elementType = elementType;        this.id = id;        this.action = action;        this.code = code;        this.property = property;    }    @XmlAttribute(name = "Action")    public String getAction() {        return action;    }    @XmlAttribute(name = "Code")    public String getCode() {        return code;    }    @XmlAttribute(name = "ID")    public String getId() {        return id;    }    @XmlElement(name = "Property")    public List<Property> getProperty() {        return property;    }    @XmlAttribute(name = "ElementType")    public String getElementType() {        return elementType;    }    public void setAction(String action) {        action = action;    }    public void setCode(String code) {        this.code = code;    }    public void setId(String id) {        this.id = id;    }    public void setProperty(List<Property> property) {        this.property = property;    }    public void setElementType(String elementType) {        this.elementType = elementType;    }    public void setObjectID(String objectID) {        objectID = objectID;    }    public void setParentCode(String parentCode) {        parentCode = parentCode;    }}