package cn.com.evo.integration.scyd.query.dto;import cn.com.evo.cms.domain.entity.cms.CatalogueRelation;import cn.com.evo.cms.domain.entity.cms.Content;import com.alibaba.fastjson.JSONObject;import com.frameworks.utils.DateUtil;import org.apache.commons.lang3.StringUtils;/** * @Description:统一搜索对象。 * @author: lu.xin * @create: 2019-04-28 3:11 PM **/public class QueryContentDto {    /**     * CP内容方的名称     */    private String cpPrvdName = "亲多多";    /**     * CP内容方的分配ID，移动分配 CPCODE,编写     */    private Integer cpPrvCode = 41000010;    /**     * CP内容方的类型，移动分配     */    private Integer cpPrvType = 1001;    /**     * 频道名称，牌照方一级频道     */    private String ChnName = "电视剧";    /**     * 频道CODE，移动分配     * 1000001-电影     * 1000002-电视剧     */    private String ChnCode = "1000002";    /**     * CP同步内容ID     */    private String contentId;    /**     * EPG内容名称，节目集名称     */    private String content;    /**     * 演员的中文全称，多个之间使用英文逗号隔开。     */    private String actors;    /**     * 导演的中文全称，多个之间使用英文逗号隔开。     */    private String directors;    /**     * 推送的内容上映的年份，格式为：yyyy-MM-dd     */    private String contentYear = "";    /**     * 注入内容所属的类型，多个之间使用英文逗号隔开。     */    private String tags;    /**     * WEB专区跳转参数，格式如：     * {     * "type": "web",     * "action": "http://xxx.xx.xx.xx:xx/abc/index.html?intent=1&psId=001"     * }     * 其中type，action不能为空，type固定为web，action为跳转至详情页面地址     */    private String intent;    /**     * 推送内容是否有效：     * 0、无效，产品下线处理     * 1、有效，上线上线处理     */    private String isEffective = "1";    /**     * 推送内容图片地址，必须是可以通过外网访问的完整http地址。（分辨率：260*360，比例：7:10，大小：100K以内）     * http://112.18.251.71:8084/image/ff80808169c23cba0169c241af3b0004.jpg     */    private String pic;    /**     * 推送内容的CP方处理的时间，格式为：yyyy-MM-dd HH:mm:ss     */    private String contentTime;    /**     * 是否付费内容，0：免费（默认）；1：付费；     */    private Integer isPaid = 1;    /**     * “4K”/“标清”/“高清”/“超清”。对视频类:高清为720p，超清为1080p的分辨率。对音乐类可以为” 高品质”/“标准” 。该值可以为空     */    private String formatType = "";    /**     * @param catalogueRelation 栏目内容关系对象     * @param contentEntity     内容对象     * @param pic               海报http地址     */    public QueryContentDto(CatalogueRelation catalogueRelation, Content contentEntity, String pic) {        this.contentId = contentEntity.getId();        this.content = contentEntity.getName();        if(StringUtils.isBlank(contentEntity.getActorTags())){            this.actors = "";        }else{            this.actors = contentEntity.getActorTags();        }        if(StringUtils.isBlank(contentEntity.getDirectorTags())){            this.directors = "";        }else{            this.directors = contentEntity.getDirectorTags();        }        this.tags = contentEntity.getClassifyTags();        this.intent = toIntent(catalogueRelation.getId());        this.pic = pic;        this.contentTime = DateUtil.getDateTime();    }    private static String toIntent(String catalogueRelationId) {        JSONObject jsonObject = new JSONObject();        jsonObject.put("type", "web");        jsonObject.put("action", "http://xxx.xx.xx.xx:xx/abc/index.html");        return jsonObject.toString();    }    public String getCpPrvdName() {        return cpPrvdName;    }    public void setCpPrvdName(String cpPrvdName) {        this.cpPrvdName = cpPrvdName;    }    public Integer getCpPrvCode() {        return cpPrvCode;    }    public void setCpPrvCode(Integer cpPrvCode) {        this.cpPrvCode = cpPrvCode;    }    public Integer getCpPrvType() {        return cpPrvType;    }    public void setCpPrvType(Integer cpPrvType) {        this.cpPrvType = cpPrvType;    }    public String getChnName() {        return ChnName;    }    public void setChnName(String chnName) {        ChnName = chnName;    }    public String getChnCode() {        return ChnCode;    }    public void setChnCode(String chnCode) {        ChnCode = chnCode;    }    public String getContentId() {        return contentId;    }    public void setContentId(String contentId) {        this.contentId = contentId;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }    public String getActors() {        return actors;    }    public void setActors(String actors) {        this.actors = actors;    }    public String getDirectors() {        return directors;    }    public void setDirectors(String directors) {        this.directors = directors;    }    public String getContentYear() {        return contentYear;    }    public void setContentYear(String contentYear) {        this.contentYear = contentYear;    }    public String getTags() {        return tags;    }    public void setTags(String tags) {        this.tags = tags;    }    public String getIntent() {        return intent;    }    public void setIntent(String intent) {        this.intent = intent;    }    public String getIsEffective() {        return isEffective;    }    public void setIsEffective(String isEffective) {        this.isEffective = isEffective;    }    public String getPic() {        return pic;    }    public void setPic(String pic) {        this.pic = pic;    }    public String getContentTime() {        return contentTime;    }    public void setContentTime(String contentTime) {        this.contentTime = contentTime;    }    public Integer getIsPaid() {        return isPaid;    }    public void setIsPaid(Integer isPaid) {        this.isPaid = isPaid;    }    public String getFormatType() {        return formatType;    }    public void setFormatType(String formatType) {        this.formatType = formatType;    }}