package cn.com.evo.integration.wasu.common.auth;import cn.com.evo.integration.common.request.AuthRequest;/** * @Description: * @author: lu.xin * @create: 2019-04-19 2:02 PM **/public class WasuAuthRequest extends AuthRequest {    public static WasuAuthRequest playAuthInit(String cardNo,                                               String appId, String columnId, String contentId,                                               String childContentId) {        WasuAuthRequest request = new WasuAuthRequest();        request.setCardNo(cardNo);        request.setAppId(appId);        request.setColumnId(columnId);        request.setContentId(contentId);        request.setChildContentId(childContentId);        return request;    }}