package cn.com.evo.integration.wasu.common.player;/** * @Description: * @author: lu.xin * @create: 2019-04-26 1:24 PM **/public class WasuPlayer {    private String folderCode;    private String contentId;    private String contentType;    public WasuPlayer(String folderCode, String contentId, String contentType) {        this.folderCode = folderCode;        this.contentId = contentId;        this.contentType = contentType;    }    public String getFolderCode() {        return folderCode;    }    public void setFolderCode(String folderCode) {        this.folderCode = folderCode;    }    public String getContentId() {        return contentId;    }    public void setContentId(String contentId) {        this.contentId = contentId;    }    public String getContentType() {        return contentType;    }    public void setContentType(String contentType) {        this.contentType = contentType;    }    public String toPlayUrl() {        return "wasu://" + folderCode + "." + contentId + "." + contentType;    }}