package cn.com.evo.integration.xjnt.common;import cn.com.evo.integration.common.request.AuthRequest;/** * @Description: * @author: lu.xin * @create: 2019-04-09 2:11 PM **/public class XjntAuthRequest extends AuthRequest {    private String stbNo;    private String stbIp;    private String accesstoken;    public static XjntAuthRequest playAuthInit(String cardNo, String stbNo, String stbIp,                                               String appId, String columnId, String contentId,                                               String childContentId, String accesstoken) {        XjntAuthRequest request = new XjntAuthRequest();        request.setCardNo(cardNo);        request.setStbIp(stbIp);        request.setStbNo(stbNo);        request.setAppId(appId);        request.setColumnId(columnId);        request.setContentId(contentId);        request.setChildContentId(childContentId);        request.setAccesstoken(accesstoken);        return request;    }    public String getStbNo() {        return stbNo;    }    public void setStbNo(String stbNo) {        this.stbNo = stbNo;    }    public String getStbIp() {        return stbIp;    }    public void setStbIp(String stbIp) {        this.stbIp = stbIp;    }    public String getAccesstoken() {        return accesstoken;    }    public void setAccesstoken(String accesstoken) {        this.accesstoken = accesstoken;    }}